mapper.update:
update bc_member
set member_pw=#{member_pw}
where member_id=#{member_id} and member_phone=#{member_phone}

update bc_member set member_pw=#{member_pw}, member_name=#{member_name}, member_phone=#{member_phone}, member_nickname=#{member_nickname},
member_img=#{member_img}, member_img_save=#{member_img_save},
last_mod_date=now() where member_idx=#{member_idx}
